<?php get_header(); ?>	<?php include (TEMPLATEPATH . '/menu.php'); ?>	<div id="content">		<?php if (have_posts()) : ?>		 <?php $post = $posts[0]; // Hack. Set $post so that the_date() works. ?><?php /* If this is a category archive */ if (is_category()) { ?>						<h2 class="pagetitle">Archivio di '<?php echo single_cat_title(); ?>' Category</h2>		 	  <?php /* If this is a daily archive */ } elseif (is_day()) { ?>		<h2 class="pagetitle">Archivio di <?php the_time('F jS, Y'); ?></h2>			 <?php /* If this is a monthly archive */ } elseif (is_month()) { ?>		<h2 class="pagetitle">Archivio di <?php the_time('F, Y'); ?></h2>		<?php /* If this is a yearly archive */ } elseif (is_year()) { ?>		<h2 class="pagetitle">Archivio del <?php the_time('Y'); ?></h2>			  <?php /* If this is a search */ } elseif (is_search()) { ?>		<h2 class="pagetitle">Risultati della ricerca</h2>			  <?php /* If this is an author archive */ } elseif (is_author()) { ?>		<h2 class="pagetitle">Archivio dell'autore</h2>		<?php /* If this is a paged archive */ } elseif (isset($_GET['paged']) && !empty($_GET['paged'])) { ?>		<h2 class="pagetitle">Archivio del giornale</h2>		<?php } ?>		<?php while (have_posts()) : the_post(); ?>		<div class="post">				<h2 id="post-<?php the_ID(); ?>"><a href="<?php the_permalink() ?>" rel="bookmark" title="Link a <?php the_title(); ?>"><?php the_title(); ?></a></h2>				<p class="postmetadata">Inserito in <?php the_category(', ') ?> | <?php edit_post_link('Edit', '', ' | '); ?>  <?php the_time('l, F jS, Y') ?> | <?php comments_popup_link('Nessun Commento &#187;', '1 Commento &#187;', '% Commenti &#187;'); ?></p> 								<div class="entry">					<?php the_content() ?>				</div>					</div>			<?php endwhile; ?>		<div class="navigation">			<div class="alignleft"><?php next_posts_link('&laquo; Articoli precedenti') ?></div>			<div class="alignright"><?php previous_posts_link('Prossimi articoli &raquo;') ?></div>		</div>		<?php else : ?>		<h2 class="center">Non Trovato</h2>		<?php include (TEMPLATEPATH . '/searchform.php'); ?>	<?php endif; ?>			</div><?php get_sidebar(); ?><?php get_footer(); ?>
