<?php get_header(); ?>	<div id="content">				  <?php if (have_posts()) : while (have_posts()) : the_post(); ?>	<?php $attachment_link = get_the_attachment_link($post->ID, true, array(450, 800)); // This also populates the iconsize for the next line ?><?php $_post = &get_post($post->ID); $classname = ($_post->iconsize[0] <= 128 ? 'small' : '') . 'attachment'; // This lets us style narrow icons specially ?>		<div class="post" id="post-<?php the_ID(); ?>">			<h2><a href="<?php echo get_permalink($post->post_parent); ?>" rev="attachment"><?php echo get_the_title($post->post_parent); ?></a> &raquo; <a href="<?php echo get_permalink() ?>" rel="bookmark" title="Link a: <?php the_title(); ?>"><?php the_title(); ?></a></h2>			<div class="entrytext">				<p class="<?php echo $classname; ?>"><?php echo $attachment_link; ?><br /><?php echo basename($post->guid); ?></p>				<?php the_content('<p class="serif">Leggi il resto di questo articolo &raquo;</p>'); ?>					<?php link_pages('<p><strong>Pagine:</strong> ', '</p>', 'number'); ?>					<p class="postmetadata alt">					<small>						Articolo inserito						<?php /* This is commented, because it requires a little adjusting sometimes.							You'll need to download this plugin, and follow the instructions:							http://binarybonsai.com/archives/2004/08/17/time-since-plugin/ */							/* $entry_datetime = abs(strtotime($post->post_date) - (60*120)); echo time_since($entry_datetime); echo ' ago'; */ ?> 						il <?php the_time('l, F jS, Y') ?> at <?php the_time() ?>						nella categoria <?php the_category(', ') ?>.						Puoi controllare le riposte attraverso questo <?php comments_rss_link('RSS 2.0'); ?> feed. 												<?php if (('open' == $post-> comment_status) && ('open' == $post->ping_status)) {							// Both Comments and Pings are open ?>							Puoi <a href="#respond">lasciare un commento</a>, od un <a href="<?php trackback_url(true); ?>" rel="trackback">trackback</a> dal tuo sito.												<?php } elseif (!('open' == $post-> comment_status) && ('open' == $post->ping_status)) {							// Only Pings are Open ?>							I commenti non sono attivi ma puoi usare un<a href="<?php trackback_url(true); ?> " rel="trackback">trackback</a> dal tuo sito.												<?php } elseif (('open' == $post-> comment_status) && !('open' == $post->ping_status)) {							// Comments are open, Pings are not ?>							Puoi andare alla fine e lasciare un commento, i ping sono attualmente disabilitati									<?php } elseif (!('open' == $post-> comment_status) && !('open' == $post->ping_status)) {							// Neither Comments, nor Pings are open ?>Sia i commenti che i ping sono disabilitati												<?php } edit_post_link('Edit this entry.','',''); ?>											</small>				</p>				</div>		</div>			<?php comments_template(); ?>		<?php endwhile; else: ?>			<p>Scusa ma nessun allegato riscontra i tuoi criteri di ricerca</p>	<?php endif; ?>		</div><?php get_footer(); ?>
